import urllib.request, urllib.parse, urllib.errorimport json# Note that Google is increasingly requiring keys# for this APIserviceurl = 'https://maps.googleapis.com/maps/api/geocode/json?'while True:    address = input('Enter location: ')    if len(address) < 1:        break    url = serviceurl + urllib.parse.urlencode(        {'address': address})        print('Retrieving', url)    uh = urllib.request.urlopen(url+'&key=AIzaSyAP3nb977m-9vX_v47vglrcTsf5-N9pFtU')    data = uh.read().decode()    print('Retrieved', len(data), 'characters')    js = json.loads(data)        if not js or 'status' not in js or js['status'] != 'OK':        print('==== Failure To Retrieve ====')        print(data)        continue    if js['status'] == 'OK': break    for item in js:                if len(item['short_name'])==2 and 'country' in item['types']:            print(item['short_name'])        continuejs_1=json.dumps(js, indent=4)print(js_1)lat = js["results"][0]["geometry"]["location"]["lat"]lng = js["results"][0]["geometry"]["location"]["lng"]print('lat', lat, 'lng', lng)location = js['results'][0]['formatted_address']c_code=["results"][0]["address_components"]["short_name"]print(location)